  useEffect(() => {
    const unsub = onAuthStateChanged(auth, (user) => {
      if (user) {
        fetchApplications(user.uid);
      }
    });

    return () => unsub();
  }, []);

  const fetchApplications = async (userId) => {
    try {
      const token = await auth.currentUser?.getIdToken();
