function UserApplications() {
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchApplications = async (userId) => {
    try {
      const res = await fetch(`http://localhost:5000/applications/${userId}`);
      const data = await res.json();
      setApplications(data);
    } catch (err) {
      console.error("Error fetching applications:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const userId = "123"; // üü° –ó–∞–º—ñ–Ω–∏ –Ω–∞ –∞–∫—Ç—É–∞–ª—å–Ω–∏–π userId (–∑ –ø—Ä–æ–ø—Å—ñ–≤, –∫–æ–Ω—Ç–µ–∫—Å—Ç—É –∞–±–æ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü—ñ—ó)
    fetchApplications(userId);
  }, []);


  if (loading) return <p>–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...</p>;

  return (
    <div>
      <h2>–ü–æ–¥–∞–Ω—ñ –∑–∞—è–≤–∫–∏</h2>
      {applications.length === 0 ? (
        <p>–£ –≤–∞—Å —â–µ –Ω–µ–º–∞—î –ø–æ–¥–∞–Ω–∏—Ö –∑–∞—è–≤–æ–∫.</p>
      ) : (
        <ul>
          {applications.map(app => (
            <li key={app.id}>
              –ó–∞—è–≤–∫–∞ –Ω–∞ –≤–∞–∫–∞–Ω—Å—ñ—é: {app.vacancyId} ‚Äî –ø–æ–¥–∞–Ω–æ: {app.appliedAt?.toDate?.().toLocaleString() || "‚Äî"}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default UserApplications;
