import React, { useEffect, useState } from "react";
import { auth } from "./backend/firebase-db";
import { onAuthStateChanged } from "firebase/auth";

function UserApplications() {
  const [user, setUser] = useState(null);
  const [applications, setApplications] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsub = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      if (currentUser) fetchApplications(currentUser.uid);
    });
    return () => unsub();
  }, []);

  const fetchApplications = async (userId) => {
    try {
      const res = await fetch(`http://localhost:5000/applications/${userId}`);
      const data = await res.json();
      setApplications(data);
    } catch (err) {
      console.error("Error fetching applications:", err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>Завантаження...</p>;

  return (
    <div>
      <h2>Подані заявки</h2>
      {applications.length === 0 ? (
        <p>У вас ще немає поданих заявок.</p>
      ) : (
        <ul>
          {applications.map(app => (
            <li key={app.id}>
              Заявка на вакансію: {app.vacancyId} — подано: {app.appliedAt?.toDate?.().toLocaleString() || "—"}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default UserApplications;
